import React, { useState, useEffect } from "react";
import axios from "axios";

const WeatherCard = () => {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState("Bhubaneswar");

  const API_KEY = "9556b40bb6f2c9a2ea7859d04f6af5d2";

  const fetchWeather = async () => {
    try {
      const res = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(res.data);
    } catch (err) {
      console.error("Error fetching weather:", err);
    }
  };

  useEffect(() => {
    const fetchWeather = async () => {
      // your API logic
    };

    fetchWeather();
  }, [city]); // or [] if it's meant to run only once


  return (
    <div className="bg-white rounded-2xl shadow-2xl p-6">
      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="border rounded px-3 py-1 w-2/3"
        />
        <button
          onClick={fetchWeather}
          className="ml-2 px-4 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          Search
        </button>
      </div>

      {weather && (
        <>
          <div className="text-center">
            <h2 className="text-2xl font-semibold">{weather.name}</h2>
            <p className="text-gray-500 capitalize">{weather.weather[0].description}</p>
            <div className="text-6xl font-bold mt-4">
              {Math.round(weather.main.temp)}°C
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4 mt-6 text-sm text-gray-700">
            <div className="bg-gray-100 rounded p-3">
              <p className="font-semibold">Humidity</p>
              <p>{weather.main.humidity}%</p>
            </div>
            <div className="bg-gray-100 rounded p-3">
              <p className="font-semibold">Wind</p>
              <p>{weather.wind.speed} m/s</p>
            </div>
            <div className="bg-gray-100 rounded p-3">
              <p className="font-semibold">Feels Like</p>
              <p>{Math.round(weather.main.feels_like)}°C</p>
            </div>
            <div className="bg-gray-100 rounded p-3">
              <p className="font-semibold">Pressure</p>
              <p>{weather.main.pressure} hPa</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default WeatherCard;
